import React from "react";
import PropTypes from "prop-types";
import TextField from '@material-ui/core/TextField';
import Grid from '@material-ui/core/Grid';
import Container from '@material-ui/core/Container';

import { theme } from "../editables/EditablesContext";

const DEFAULT_BACKGROUND_IMAGE = "data:image/jpeg;base64,"


const styles = {
  container: {
    padding: "8px 4px",
    backgroundColor: "#fff",
    borderRadius: "8px",
  },
  image: {
    marginTop: "0.5rem",
    width: "100%",
    maxWidth: "250px",
  },
  button: {
    cursor: "pointer",
    background: theme.primaryColor,
    border: `1px solid ${theme.primaryColor}`,
    color: '#000000',
    display: "inline-flex",
    padding: "6px 12px",
    fontSize: `14px`,
    fontFamily: theme.fontFamily,
    borderRadius: "2px",
    "&:hover, &:focus": {
      background: theme.primaryColor,
    },
  },
  hidden: {
    display: "none !important"
  },
  label: {
    color: '#000000',
    marginRight: "4px",
    marginTop: '10px'
  },
  textField: {
    width: "100%",
    fontSize: "inherit",
    fontFamily: "inherit",
    fontWeight: "inherit",
    color: "rgba(0,0,0,0.8)",
    backgroundColor: "#fff",
  },
  input: {
    borderRadius: '0'
  },
  preview: {
    height: '180px',
    width: '100%',
    maxWidth: '180px',
    position: 'relative',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    backgroundSize: 'cover',
    backgroundPosition: 'center',
    backgroundRepeat: 'no-repeat',
    margin: '0 auto',
  }
};

class ImageUploadEditor extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      loading: false,
      imageError: false,
      preview: this.props.content.imageSrc,
    };
  }

  handleCaptionChange = event => {
    const caption = event.currentTarget.value;

    this.props.onContentChange({
      ...this.props.content,
      caption: caption
    })
  }

  handleTitleChange = event => {
    const title = event.currentTarget.value;

    this.props.onContentChange({
      ...this.props.content,
      title: title
    })
  }

  handleImageChange = event => {
    this.setState({ loading: true, imageError: false, preview: null });

    if (!event.target.files) {
      this.setState({ loading: false, imageError: false, preview: null });
    }

    const image = event.target.files[0];

    if (!image) {
      this.setState({
        loading: false
      });
      return;
    }

    if (image.size > this.props.maxSize) {
      this.setState({
        imageError: true,
        loading: false
      });
      return;
    }

    this.props.uploadImage(image).then(imageUrl => {
      this.props.onContentChange({
        ...this.props.content,
        image: image,
        imageSrc: imageUrl
      })

      this.setState({
        preview: imageUrl,
        loading: false
      })
    })
  }

  render() {
    const { showCaption, editCaption, EditorProps, content } = this.props;
    const { caption, title } = content;
    const previewImage = this.state.preview || DEFAULT_BACKGROUND_IMAGE

    return (
      <Container maxWidth="sm">
        <Grid container spacing={1} style={styles.container}>
          <Grid item xs={12}>
            <div className="image-preview" style={{ ...styles.preview, backgroundImage: `url(${previewImage})` }}>
              <label style={this.state.preview ? {...styles.button, background: '#fff' } : styles.button}>
                {this.state.loading ? (
                  <div className="loader-container">
                    <div className="loader">loading...</div>
                  </div>) :
                  this.state.preview ? 'Change image' : 'Select image'
                }
                <input
                  className="hidden"
                  hidden={true}
                  type="file"
                  accept="image/*"
                  style={styles.hidden}
                  onChange={this.handleImageChange}
                  {...EditorProps.image}
                />
              </label>
            </div>
              {
                this.state.imageError &&
                <div>{`Your file is too big. Please select a file less than ${parseInt(this.props.maxSize) / (1024*1024)}MB.`}</div>
              }
          </Grid>
          <Grid item xs={12}>
            <TextField
              id="image-alt-text"
              label="Image title (alt text)"
              value={ title }
              onChange={this.handleTitleChange}
              autoFocus={true}
              variant="outlined"
              size="small"
              margin="dense"
              InputProps={{ style: styles.input }}
              style={styles.textField}
              { ...EditorProps.anchor }
            />
            {(showCaption || editCaption) && (
              <TextField
                id="image-caption"
                label="Caption"
                value={ caption }
                onChange={this.handleCaptionChange}
                variant="outlined"
                size="small"
                margin="dense"
                InputProps={{ style: styles.input }}
                style={styles.textField}
                { ...EditorProps.caption }
              />
            )}
          </Grid>
        </Grid>
      </Container>
    );
  }
}

ImageUploadEditor.propTypes = {
  content: PropTypes.shape({ imageSrc: PropTypes.string, caption: PropTypes.string, title: PropTypes.string }).isRequired,
  classes: PropTypes.string,
  EditorProps: PropTypes.shape({ image: PropTypes.object, caption: PropTypes.object, title: PropTypes.object }),
  uploadImage: PropTypes.func,
  onContentChange: PropTypes.func.isRequired
}

ImageUploadEditor.defaultProps = {
  content: { imageSrc: DEFAULT_BACKGROUND_IMAGE, caption: "", title: "" },
  EditorProps: { image: {}, caption: {}, title: {} },
  uploadImage: image => console.log('Implement a Promise to save file and return URL.', image),
  onContentChange: url => console.log('Implement a function to save content changes.', url),
}


export default ImageUploadEditor;
